    public static String getPhoneNumber(Context context){
        TelephonyManager tMgr = (TelephonyManager) context.getSystemService(Context.TELEPHONY_SERVICE);
        String mPhoneNumber = tMgr.getLine1Number();

        return mPhoneNumber;
    }

    public static int getMapType(String mapType){
        switch (mapType){
            case "Normal": return GoogleMap.MAP_TYPE_NORMAL;
            case "Satellite": return GoogleMap.MAP_TYPE_SATELLITE;
            case "Hybrid": return GoogleMap.MAP_TYPE_HYBRID;
            case "Terrain": return GoogleMap.MAP_TYPE_TERRAIN;

            default: return GoogleMap.MAP_TYPE_NORMAL;
        }
    }

    public static ArrayList<String> readContacts(Context context, ArrayList<String> phoneNumbers) {
        ArrayList<String> usersFromAgenda = new ArrayList<String>();
        ContentResolver contentResolver = context.getContentResolver();
        Cursor cursor = contentResolver.query(ContactsContract.Contacts.CONTENT_URI, null,
                null, null, null);

        if (cursor.getCount() > 0) {
            /* Read one contact */
            while (cursor.moveToNext()) {
                String contactId = cursor.getString(cursor
                        .getColumnIndex(ContactsContract.Contacts._ID));
                Cursor phones = contentResolver.query(ContactsContract.CommonDataKinds.Phone.CONTENT_URI, null,
                        ContactsContract.CommonDataKinds.Phone.CONTACT_ID + " = " + contactId, null, null);

                /* Every contact can have at least one phone number */
                while (phones.moveToNext()) {
                    String number = phones.getString(phones
                            .getColumnIndex(ContactsContract.CommonDataKinds.Phone.NUMBER));
                    if (number.length() > 10) {
                        number = number.substring(number.length() - 10, number.length());
                    }

                    if (phoneNumbers.contains(number)) {
                        usersFromAgenda.add(number);
                    }
                }

                phones.close();
            }
        }

        return usersFromAgenda;
    }

